mapper.insert:
insert into tb_order (OrderId, Id,CustomerId, Orderdate,
  Ordermount, message, postmethod,
  paymethod, recevername, receveraddr,
  recevertel, memo, totalprice
  )
values (#{orderid},#{id}, #{customerid}, now(),
  #{ordermount}, #{message}, #{postmethod},
  #{paymethod}, #{recevername}, #{receveraddr},
  #{recevertel}, #{memo}, #{totalprice}
  )
