mapper.insert:
insert into tb_comment (CommentId, BookId, CustomerId, 
  Commentname, Commentdate, commentcontent
  )
values (#{commentid,jdbcType=CHAR}, #{bookid,jdbcType=INTEGER}, #{customerid,jdbcType=CHAR}, 
  #{commentname,jdbcType=VARCHAR}, now(), #{commentcontent,jdbcType=VARCHAR}
  )

mapper.select:
select

from tb_comment
where
BookId=#{bookid}
and
CustomerId=#{customerid}

mapper.insert:
insert into tb_comment (CommentId, BookId, CustomerId,
  Commentname, Commentdate, commentcontent
  )
values (#{commentid}, #{bookid}, #{customerid},
  #{customername}, now(), #{commentcontent}
  )

mapper.update:
update tb_comment
set
  commentcontent = #{commentcontent}
where CommentId = #{commentid}
