mapper.select:
select

from tb_customer
where
CustomerID=#{customerid}
and
CustomerPwd=#{customerpwd}

mapper.insert:
insert into tb_customer (CustomerID, CustomerName, CustomerPwd,
  Customertruename, CustomerSex, CustomerTel,
  CustomerAddr, CustomerRegTime, CustomerQuer,
  CustomerAnswer
  )
values (#{customerid}, #{customername}, #{customerpwd},
  #{customertruename}, #{customersex}, #{customertel},
  #{customeraddr}, #{customerregtime}, #{customerquer},
  #{customeranswer}
  )

mapper.update:
update tb_customer
set CustomerPwd = #{customerpwd}
where CustomerID = #{customerid}

update tb_customer
set CustomerQuer = #{customerquer},
  CustomerAnswer = #{customeranswer}
where CustomerID = #{customerid}
