mapper.insert:
insert into tb_shopbook (ShopbookId, CustomerId, BookId, 
  ordermount, price, ispay
  )
values (#{shopbookid,jdbcType=CHAR}, #{customerid,jdbcType=CHAR}, #{bookid,jdbcType=INTEGER}, 
  #{ordermount,jdbcType=INTEGER}, #{price,jdbcType=INTEGER}, #{ispay,jdbcType=VARCHAR}
  )

mapper.update:
update tb_shopbook
set
  ordermount = #{ordermount},
  price = #{price}
where ShopbookId = #{shopbookid}

mapper.insert:
insert into tb_shopbook (ShopbookId, CustomerId, BookId,
  ordermount, price, ispay
  )
values (#{shopbookid}, #{customerid}, #{bookid},
  #{ordermount}, #{price}, #{ispay}
  )

mapper.select:
select

from tb_shopbook
where
CustomerId = #{customerid}
and
BookId=#{bookid}
